import { useContext, useEffect } from "react"
import { Link, useParams } from "react-router-dom"
import Spinner from "../../asset/spinner/spinner.component"
import { GithubContext } from "../../context/github/githubContext"




const User = ()=>{

    const {user,getSingleUser,isLoading} = useContext(GithubContext)
    
    const params = useParams()

    useEffect(() => {
        getSingleUser(params.login)
        // eslint-disable-next-line
    },[])


    if(!isLoading){
        return(
            <div className="container mx-auto">
                <Link to={"/"} className="text-white text-[16px]">Back To Search</Link>
                <div className="flex">
                    <div className="bg-red- h-20"></div>
                    <div className="bg-yellow-600"></div>
                </div>
            </div>
        )
    }else{
        return <Spinner/>
    }
}

export default User
