import { useContext, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import Spinner from "../../asset/spinner/spinner.component";
import { GithubContext } from "../../context/github/githubContext";

const User = () => {
  const { user, getSingleUser, isLoading } = useContext(GithubContext);

  const params = useParams();

  useEffect(() => {
    getSingleUser(params.login);
    // eslint-disable-next-line
  }, []);

  console.log(user.avatar_url);

  if (!isLoading) {
    return (
      <div className="container mx-auto">
        <Link to={"/"} className="text-white text-[16px] p-4">
          Back To Search
        </Link>
        <div className="grid  place-items-center md:place-items-start gap-6 md:px-0 px-10 mt-3 md:grid-cols-[250px_1fr]">
          <div className="relative">
            {/* Profile image */}
          <img
            src={`${user.avatar_url}`}
            alt="Avater"
            className="h-[16rem] w-full shadow-md border-black  rounded object-cover"
          />
          <div className="absolute bottom-3 left-6">
            <h3 className="text-white font-bold">{user.name}</h3>
            <p className="text-white">{user.login}</p>
          </div>
          </div>
          <div className=" w-full">
            {/* Profile Details */}
            <div className="flex gap-2 items-center">
              <h2 className="text-white text-[2rem] font-bold">{user.name}</h2>
              <div className="shadow-md border-black text-green-400 rounded-xl p-1">{user.type}</div>
              <div className="shadow-md border-black text-blue-300 rounded-xl p-1">{user.location}</div>
            </div>
            <div>
              <p className="text-white py-4">{user.bio}</p>
            </div>
            <div className="text-center md:text-start">
              <a href={`${user.html_url}`} target="_blank" rel="noreferrer">
                <button type="button" class="text-white  hover:text-black border border-white hover:bg-white font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-2 mb-2">View Github Profile</button>
              </a>
            </div>
            <div className="grid gap-2 grid-cols-3 mt-4 border-black rounded p-4 shadow-md">
              <div className=" border-r-2 border-[#D8D2CB]">
                <p className="text-[#D5D5D5]">Location</p>
                <h2 className="">{user.location}</h2>
              </div>
              <div className="border-r-2 border-[#D8D2CB]">
                <p>Website</p>
                <h2>{user.blog}</h2>
              </div>
              <div>
                <p>Twitter</p>
                <h2>{user.twitter_username}</h2>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return <Spinner />;
  }
};

export default User;
