import { useContext, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import Spinner from "../../asset/spinner/spinner.component";
import { GithubContext } from "../../context/github/githubContext";

const User = () => {
  const { user, getSingleUser, isLoading } = useContext(GithubContext);

  const params = useParams();

  useEffect(() => {
    getSingleUser(params.login);
    // eslint-disable-next-line
  }, []);

  console.log(user.avatar_url);

  if (!isLoading) {
    return (
      <div className="container mx-auto">
        <Link to={"/"} className="text-white text-[16px] p-4">
          Back To Search
        </Link>
        <div className="grid  place-items-center gap-3">
          <img
            src={`${user.avatar_url}`}
            alt="Avater"
            className="h-[16rem]  rounded object-cover"
          />
          <div className="bg-yellow-600 h-20 w-full"></div>
        </div>
      </div>
    );
  } else {
    return <Spinner />;
  }
};

export default User;
