const Pagination = ({ postsPerPage, totalPosts, paginate }) => {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <ul className='pagination flex gap-4 justify-center'>
        {pageNumbers.map(number => (
          <button key={number} className='page-item px-4 bg-slate-800 rounded-full text-white hover:text-b'>
            <a onClick={() => paginate(number)} href='!#' className='page-link'>
              {number}
            </a>
          </button>
        ))}
      </ul>
    </nav>
//     
  );
};

export default Pagination;
