import { useEffect, useState } from "react"
import Spinner from "../asset/spinner/spinner.component"
import UserItem from "./userItem.component"


const UserResults = ()=>{

    const [userResults,setUserResults] = useState([])
    const [isLoading,setIsLoading] = useState(true)

    useEffect(()=>{
        
    const data = async ()=>{
        const info = await fetch("https://api.github.com/users",{
          headers: {
            Authorization: "token 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"
          }
        })
    
        const res = await info.json()
    
        setUserResults(res)
        setIsLoading(false)
      }
  
      setTimeout(()=>{data()},5000)
    },[])

    if(!isLoading){
        return (
            <div className="grid gap-4 grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                {userResults.map(e=><UserItem />)}
            </div>
        )
    }else{
        return (<Spinner/>)
    }
}

export default UserResults
