import { useContext, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import Spinner from "../../asset/spinner/spinner.component";
import { GithubContext } from "../../context/github/githubContext";

const User = () => {
  const { user, getSingleUser, isLoading } = useContext(GithubContext);

  const params = useParams();

  useEffect(() => {
    getSingleUser(params.login);
    // eslint-disable-next-line
  }, []);

  console.log(user.avatar_url);

  if (!isLoading) {
    return (
      <div className="container mx-auto">
        <Link to={"/"} className="text-white text-[16px] p-4">
          Back To Search
        </Link>
        <div className="grid  place-items-center md:place-items-start gap-6 md:px-0 px-10 mt-3 md:grid-cols-[400px_1fr]">
          <img
            src={`${user.avatar_url}`}
            alt="Avater"
            className="h-[16rem] w-full shadow-md border-black  rounded object-cover"
          />
          <div className=" h-20 w-full">
            <div className="flex gap-2 items-center">
              <h2 className="text-white text-[2rem]">{user.name}</h2>
              <div className="shadow-md border-black text-green-400 rounded-xl p-1">{user.type}</div>
              <div className="shadow-md border-black text-blue-300 rounded-xl p-1">{user.location}</div>
            </div>
            <div>
              <p className="text-white py-6">{user.bio}</p>
            </div>
            <button type="button" class="text-white hover:text-black border border-white hover:bg-white focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center mr-2 mb-2 dark:border-blue-500 dark:text-blue-500 dark:hover:text-white dark:hover:bg-blue-600 dark:focus:ring-blue-800">Default</button>
          </div>
        </div>
      </div>
    );
  } else {
    return <Spinner />;
  }
};

export default User;
